// src/components/CryptoMap.jsx
import React, { useState } from "react";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
} from "react-simple-maps";
import { scaleLinear } from "d3-scale";
import ReactTooltip from "react-tooltip";

// Mock trending data
const trendingData = [
  {
    country: "United States",
    coordinates: [-100.0, 38.0],
    coin: "Bitcoin",
    platform: "X",
    meme: "ðŸš€ BTC to the moon!",
  },
  {
    country: "Japan",
    coordinates: [138.2529, 36.2048],
    coin: "Shiba Inu",
    platform: "TikTok",
    meme: "ðŸ¶ SHIB trending again!",
  },
  {
    country: "Germany",
    coordinates: [10.4515, 51.1657],
    coin: "Ethereum",
    platform: "X",
    meme: "ðŸ”¥ ETH powering AI projects",
  },
];

const colorScale = scaleLinear()
  .domain([0, trendingData.length])
  .range(["#0ff", "#0f0"]);

const CryptoMap = () => {
  const [tooltip, setTooltip] = useState("");

  return (
    <div className="bg-white/10 p-6 rounded-2xl shadow-xl backdrop-blur-md">
      <h2 className="text-2xl font-bold mb-4 text-cyan-300">Trend by Region</h2>
      <p className="text-gray-300 text-sm mb-6">
        Explore where crypto coins are trending and on which platform (X or TikTok).
        Hover over the markers to see real-time memes, stats, and more.
      </p>
      <div className="w-full overflow-x-auto">
        <ReactTooltip>{tooltip}</ReactTooltip>
        <ComposableMap
          projectionConfig={{ scale: 140 }}
          className="w-full h-auto"
        >
          <Geographies geography="https://cdn.jsdelivr.net/npm/world-atlas@2/countries-110m.json">
            {({ geographies }) =>
              geographies.map((geo) => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill="#1a1a1a"
                  stroke="#0ff"
                  strokeWidth={0.1}
                  style={{
                    default: { outline: "none" },
                    hover: { fill: "#333", outline: "none" },
                    pressed: { outline: "none" },
                  }}
                />
              ))
            }
          </Geographies>

          {trendingData.map((location, index) => (
            <Marker
              key={index}
              coordinates={location.coordinates}
              onMouseEnter={() => {
                setTooltip(
                  `<strong>${location.country}</strong><br/>
                   Coin: ${location.coin}<br/>
                   Platform: ${location.platform}<br/>
                   Meme: ${location.meme}`
                );
              }}
              onMouseLeave={() => {
                setTooltip("");
              }}
            >
              <circle
                r={8}
                fill={location.platform === "TikTok" ? "#ff0050" : "#1da1f2"}
                stroke="#fff"
                strokeWidth={1}
              />
            </Marker>
          ))}
        </ComposableMap>
      </div>
    </div>
  );
};

export default CryptoMap;
